q1:Exec.q1:Arguments:
-NoProfile -NonInteractive -Command (Invoke-Command -ScriptBlock { Import-Module ActiveDirectory
$localDomainController = Get-ADDomainController 127.0.0.1 $currentDomain = Get-ADDomain $currentDomainDn = $currentDomain.DistinguishedName $global:EnvMachineName = $env:computername $global:OsVersion = [System.Environment]::OSVersion.Version $global:OsVersion = [System.String]::Concat($global:OsVersion.Major, """.""", $global:OsVersion.Minor, """.""", $global:OsVersion.Build) $global:LocalDomainControllerDNSName = $localDomainController.HostName $global:LocalDomainControllerSite = $localDomainController.Site $global:PrimaryDomainControllerFilter = $Null If ($Null -ne $currentDomain.PDCEmulator) { $global:PrimaryDomainControllerFilter = $currentDomain.PDCEmulator } Else { $global:PrimaryDomainControllerFilter = '10.100.0.10' } $global:PrimaryDomainControllerSite = (Get-ADDomainController $global:PrimaryDomainControllerFilter).Site $global:AuditPath = '\\__DNSNAME__\sysvol\alsid.corp\Policies\{ef253028-09b7-4f00-9b7b-c2b470398b00}\Machine\Microsoft\Windows NT\Audit\' $global:AuditPath = $global:AuditPath.replace("""__DNSNAME__""", $global:LocalDomainControllerDNSName) $global:AuditCSVPath = $global:AuditPath + 'audit.csv' $global:GptTmplInfPath = '\\__DNSNAME__\sysvol\alsid.corp\Policies\{ef253028-09b7-4f00-9b7b-c2b470398b00}\Machine\Microsoft\Windows NT\SecEdit\GptTmpl.inf' $global:GptTmplInfPath = $global:GptTmplInfPath.replace("""__DNSNAME__""", $global:LocalDomainControllerDNSName) $global:RegistryPolPath = '\\__DNSNAME__\sysvol\alsid.corp\Policies\{ef253028-09b7-4f00-9b7b-c2b470398b00}\Machine\Registry.pol' $global:RegistryPolPath = $global:RegistryPolPath.replace("""__DNSNAME__""", $global:LocalDomainControllerDNSName) $envTemp = (Get-Item $env:TEMP).Fullname $global:TemporaryTaskFolderLocation = $envTemp + '\TenableADTask_' + [guid]::NewGuid() + '\' $global:TempAuditCsvFileLocation = $global:TemporaryTaskFolderLocation + 'audit.csv' $sysvolLocationRegKey = Get-Item -LiteralPath 'HKLM:\SYSTEM\CurrentControlSet\Services\Netlogon\Parameters' -ErrorAction Stop $defaultSysvolPath = $sysvolLocationRegKey.GetValue('Sysvol', $Null) $global:IoaFolderLocation = $defaultSysvolPath + """\\""" + $currentDomain.DnsRoot + """\Policies\{ef253028-09b7-4f00-9b7b-c2b470398b00}\Machine\IOA""" $global:GptIniLocation = $defaultSysvolPath + """\\""" + $currentDomain.DnsRoot + """\Policies\{ef253028-09b7-4f00-9b7b-c2b470398b00}\gpt.ini""" $global:DeleteOldGpo = $False $global:OldGpoFolder = $defaultSysvolPath + """\\""" + $currentDomain.DnsRoot + """\Policies\{__OLDGPOID__}""" $global:Channels = @('Microsoft-Windows-NTLM/Operational','Microsoft-Windows-EFS/Debug','Microsoft-Windows-DNSServer/Audit','Microsoft-Windows-Sysmon/Operational','Microsoft-Windows-WMI-Activity/Operational')
function Register-Channels { try { If ($True) { Write-Host """[-] Channels will be registered by the listener""" } Else { Write-Host """[-] Registering channels...""" foreach ($channel in $global:Channels) { Write-Host """[-] Registering: """ $channel (Get-Item HKLM:\).OpenSubKey("""SYSTEM""", $True).CreateSubKey("""CurrentControlSet\Services\EventLog\\""" + $channel) } } } catch { Write-Error $_.Exception } }
function Register-EventListener { process { try { Write-Host """[-] Running executable Register-TenableADEventsListener.exe""" $executableLocation = $global:IoaFolderLocation + '\Register-TenableADEventsListener.exe'
$domainControllerEventLogFileLocation = $global:IoaFolderLocation + '\' + $global:EnvMachineName + '-' + $global:OsVersion + '-0_' + '.gz' Write-Host """[-] Events will be written to path: $domainControllerEventLogFileLocation"""
# Events file write frequency depends upon the fact current DC is in PDC site and FRS is still in use $ioaFileWriteFrequencyInSeconds = 15 try { $dfsrFilter = 'DistinguishedName -eq """CN=DFSR-GlobalSettings,' + $((Get-ADDomain).SystemsContainer) + '"""' $dfsrObject = Get-ADObject -Filter $dfsrFilter -Properties msDFSR-Flags If($Null -ne $dfsrObject -And $dfsrObject.'msDFSR-Flags' -eq 48) { # 48 means DFSR is active Write-Host """[-] Current domain controller replication process is handled by DFSR""" } Else { Write-Host """[-] Current domain controller replication is (still) handled by FRS instead of DFSR""" If($global:LocalDomainControllerSite -eq $global:PrimaryDomainControllerSite) { Write-Host """[-] Current domain controller is in primary domain controller site ($global:PrimaryDomainControllerSite): intra-site replication applies""" } Else { Write-Host """[-] Current domain controller site ($global:LocalDomainControllerSite) is in a site different from primary domain controller site ($global:PrimaryDomainControllerSite): inter-site replication applies""" $interSitesIpDn = """CN=IP,CN=Inter-Site Transports,CN=Sites,CN=Configuration,""" + $currentDomainDn $repAdminOutput = repadmin /showism $interSitesIpDn /verbose $repInterval = Get-InterSitesReplicationIntervalMinutes -Output $repAdminOutput -SourceSiteDn $global:LocalDomainControllerSite -DestinationSiteDn $global:PrimaryDomainControllerSite $fileWriteFrequencyInMinutes = [int]$repInterval + 5 $ioaFileWriteFrequencyInSeconds = $fileWriteFrequencyInMinutes * 60 Write-Host """[-] Shortest path found between sites $global:LocalDomainControllerSite and $global:PrimaryDomainControllerSite : events file will be written every $fileWriteFrequencyInMinutes minutes""" } } } catch { Write-Error $_.Exception }
$domainControllerEventLogFileLocationString = '"""' + $domainControllerEventLogFileLocation + '"""' $auditPath = $global:AuditPath.TrimEnd('\') $auditPathString = '"""' + $auditPath + '"""' $administratorSid = (Get-ADDomain).DomainSID.Value + "-500" $administrator = Get-ADUser -Identity $administratorSid $administratorName = $administrator.SamAccountName $pdcName = 'QASAAS-AFAD-DC1' $domainName = 'alsid.corp' $domainControllerDnsName = 'qasaas-afad-dc1.alsid.corp' $gptTmplInfPath = $global:GptTmplInfPath.TrimEnd('\') $gptTmplInfPathString = '"""' + $gptTmplInfPath + '"""' $registryPolPath = $global:RegistryPolPath.TrimEnd('\') $registryPolPathString = '"""' + $registryPolPath + '"""' $executableArguments = @('-t',$ioaFileWriteFrequencyInSeconds,'-p',$domainControllerEventLogFileLocationString,'-g','-w','-f',$auditPathString,'-a',$administratorName,'-i',$pdcName,'-y',$domainName,'-z',$domainControllerDnsName,'-x',$gptTmplInfPathString,'-l',$registryPolPathString) $startedProcessPid = (Start-Process -PassThru -WorkingDirectory $global:IoaFolderLocation -WindowStyle Hidden -FilePath $executableLocation -ArgumentList $executableArguments).Id Register-RestartListener -IoaExeEngineName $executableLocation -IoaFolderPath $global:IoaFolderLocation -EventsFilePath $domainControllerEventLogFileLocation -FileWriteIntervalInSeconds $ioaFileWriteFrequencyInSeconds -StartedProcessId $startedProcessPid -AuditPath $auditPath -AdminName $administratorName -PdcName $pdcName -DomainName $domainName -DomainControllerDnsName $domainControllerDnsName -GptTmplInfPath $gptTmplInfPath -RegistryPolPath $registryPolPath } catch { Write-Error $_.Exception } } }
function Get-InterSitesReplicationIntervalMinutes { param( [Parameter(Mandatory = $true)] [Object[]] $Output, [Parameter(Mandatory = $true)] [String] $SourceSiteDn, [Parameter(Mandatory = $true)] [String] $DestinationSiteDn )
$lines = ($Output | Select-String -Pattern """$SourceSiteDn""" | Select-String -Pattern """$DestinationSiteDn""") If ($lines.Length -le 0 -Or -Not($lines[0].Line.EndsWith("""):"""))) { return 15 }
Write-Host """[-] Connection found in showism output:""" Write-Host $lines[0]
return ($lines[0].Line.Split(' '))[-1].Split(')')[0] }
function Register-RestartListener { param( [Parameter(Mandatory = $true)] [String] $IoaExeEngineName, [Parameter(Mandatory = $true)] [String] $IoaFolderPath, [Parameter(Mandatory = $true)] [String] $EventsFilePath, [Parameter(Mandatory = $true)] [String] $AuditPath, [Parameter(Mandatory = $true)] [String] $AdminName, [Parameter(Mandatory = $true)] [String] $PdcName, [Parameter(Mandatory = $true)] [String] $DomainName, [Parameter(Mandatory = $true)] [String] $DomainControllerDnsName, [Parameter(Mandatory = $true)] [String] $GptTmplInfPath, [Parameter(Mandatory = $true)] [String] $RegistryPolPath, [Parameter(Mandatory = $true)] [Int] $FileWriteIntervalInSeconds, [Parameter(Mandatory = $true)] [Int] $StartedProcessId, [Parameter(Mandatory = $false)] [Int] $WmiTriggerPeriodInSec = 600 )
# Remove old WMI consumer $FilterParams = @{ 'Namespace' = 'root\subscription' 'Class' = '__EventFilter' 'Filter' = """Name = 'AlsidForAD-Launcher'""" } $filter = Get-WmiObject @FilterParams -ErrorAction SilentlyContinue | Remove-WmiObject -ErrorAction SilentlyContinue Write-Host """[-] Existing WMI filter AlsidForAD-Launcher removed""" $ConsumerParams = @{ 'Namespace' = 'root\subscription' 'Class' = 'ActiveScriptEventConsumer' 'Filter' = """Name = 'AlsidForAD-Launcher'""" } $consumer = Get-WmiObject @ConsumerParams -ErrorAction SilentlyContinue | Remove-WmiObject -ErrorAction SilentlyContinue Write-Host """[-] Existing WMI consumer AlsidForAD-Launcher removed""" Get-WmiObject -Class '__FilterToConsumerBinding' -Namespace 'root\subscription' -Filter """Filter = """"""__EventFilter.name='AlsidForAD-Launcher'""""""""" | Remove-WmiObject -ErrorAction SilentlyContinue Write-Host """[-] Existing WMI binding AlsidForAD-Launcher removed"""
# Kill running IOA engines Get-WmiObject Win32_Process -Filter """name = 'powershell.exe'""" | Where-Object { ($_.CommandLine -match """alsid""" -Or $_.CommandLine -match """Register-TenableADWMIListener""") -And $_.ProcessId -ne $pid -And $_.ProcessId -ne $StartedProcessId } | ForEach-Object { Stop-Process -Id $_.ProcessId -Force } Get-WmiObject Win32_Process -Filter """name = 'Register-TenableADEventsListener.exe'""" | Where-Object { $_.ProcessId -ne $pid -And $_.ProcessId -ne $StartedProcessId } | ForEach-Object { Stop-Process -Id $_.ProcessId -Force }
# Register new WMI consumer $LauncherFilterQuery = """Select * From __InstanceModificationEvent Within $WmiTriggerPeriodInSec Where Targetinstance ISA 'Win32_PerfFormattedData_PerfOS_System' AND TargetInstance.SystemUpTime >= 300 AND TargetInstance.SystemUpTime < 37800"""
$WmiLauncherFilterParams = @{ 'Class' = '__EventFilter' 'Namespace' = 'root\subscription' 'Arguments' = @{ Name = 'AlsidForAD-Launcher'; EventNameSpace = 'root\cimv2'; QueryLanguage = 'WQL'; Query = $LauncherFilterQuery } } $WmiLauncherEventFilterPath = Set-WmiInstance @WmiLauncherFilterParams -ErrorAction Stop Write-Host """[-] WMI filter AlsidforAD-Launcher registered"""
$command = """Start-Process -WorkingDirectory '$IoaFolderPath' -WindowStyle Hidden -FilePath '$IoaExeEngineName' -ArgumentList @('-t','$FileWriteIntervalInSeconds','-p','$EventsFilePath','-g','-w','-f','""""""$AuditPath""""""','-a','$AdminName','-i','$PdcName','-y','$domainName','-z','$domainControllerDnsName','-x','""""""$GptTmplInfPath""""""','-l','""""""$RegistryPolPath""""""')""" $bytes = [System.Text.Encoding]::Unicode.GetBytes($command) $encodedCommand = [Convert]::ToBase64String($bytes)
$LauncherEventConsumerArgs = @{ Name = 'AlsidForAD-Launcher' ScriptingEngine = 'VBScript' ScriptText = ' Dim fso, objShell, psCmdLine, encodedPwCommand Set fso = CreateObject("""Scripting.FileSystemObject""") Set objShell = CreateObject("""Wscript.shell""") ioaEnginePath = """' + $IoaExeEngineName + '""" encodedPwCommand = """' + $encodedCommand + '""" psCmdLine = """powershell.exe -encodedCommand """ + encodedPwCommand
Function IsAlsidIoaRunning Set objWMIService = GetObject("""winmgmts:\\.\root\cimv2""") Set colItems = objWMIService.ExecQuery ("""Select * from Win32_Process Where Name = """ & Chr(39) & """Register-TenableADEventsListener.exe""" & Chr(39)) For Each objItem in colItems IsAlsidIoaRunning = true Exit Function Next IsAlsidIoaRunning = false End Function
If fso.FileExists(ioaEnginePath) Then If (IsAlsidIoaRunning() = false) Then objShell.run psCmdLine, 0 End If End If ' } $WmiLauncherConsumerParams = @{ Namespace = 'root/subscription' Class = 'ActiveScriptEventConsumer' Arguments = $LauncherEventConsumerArgs } $WmiLauncherConsumer = Set-WmiInstance @WmiLauncherConsumerParams -ErrorAction Stop Write-Host """[-] WMI consumer AlsidforAD-Launcher registered"""
$WmiLauncherFilterConsumerParams = @{ 'Class' = '__FilterToConsumerBinding' 'Namespace' = 'root\subscription' 'Arguments' = @{ Filter = $WmiLauncherEventFilterPath; Consumer = $WmiLauncherConsumer } } Set-WmiInstance @WmiLauncherFilterConsumerParams -ErrorAction Stop | Out-Null Write-Host """[-] WMI binding AlsidforAD-Launcher registered""" }
function Generate-AuditCsv { process { try { # Create the audit.csv with Tenable.ad required logging settings
$AuditCSV = @' Machine Name,Policy Target,Subcategory,Subcategory GUID,Inclusion Setting,Exclusion Setting,Setting Value ,System,Audit Logoff,{0cce9216-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit Sensitive Privilege Use,{0CCE9228-69AE-11D9-BED3-505054503030},Success and Failure,,3 ,System,Audit Logon,{0cce9215-69ae-11d9-bed3-505054503030},Success and Failure,,3 ,System,Audit Process Termination,{0CCE922C-69AE-11D9-BED3-505054503030},Success,,1 ,System,Audit Kerberos Service Ticket Operations,{0cce9240-69ae-11d9-bed3-505054503030},Success and Failure,,3 ,System,Audit Kerberos Authentication Service,{0cce9242-69ae-11d9-bed3-505054503030},Success and Failure,,3 ,System,Audit Handle Manipulation,{0cce9223-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit Other Object Access Events,{0cce9227-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit SAM,{0cce9220-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit Detailed File Share,{0cce9244-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit Process Creation,{0CCE922B-69AE-11D9-BED3-505054503030},Success,,1 ,System,Audit Credential Validation,{0cce923f-69ae-11d9-bed3-505054503030},Success and Failure,,3 ,System,Audit Security Group Management,{0cce9237-69ae-11d9-bed3-505054503030},Success,,1 ,System,Audit Application Generated,{0CCE9222-69AE-11D9-BED3-505054503030},Success,,1 ,System,Audit Directory Service Access,{0cce923b-69ae-11d9-bed3-505054503030},Success,,1 '@
If (!(Test-Path -Path $global:TemporaryTaskFolderLocation)) { New-Item -ItemType Directory -Path $global:TemporaryTaskFolderLocation -ErrorAction Stop | Out-Null Write-Host """[-] Temporary folder $global:TemporaryTaskFolderLocation created""" } $RsopFilePath = $global:TemporaryTaskFolderLocation + 'rsop.xml'
Write-Host """[-] Generating RSoP file at $RsopFilePath""" gpresult.exe /SCOPE COMPUTER /X $RsopFilePath If (!(Test-Path $RsopFilePath)) { Write-Host """[-] Generating RSoP file at $RsopFilePath failed""" $administratorSid = (Get-ADDomain).DomainSID.Value + "-500" $administrator = Get-ADUser -Identity $administratorSid $administratorName = $administrator.SamAccountName Write-Host """[-] Generating RSoP file at $RsopFilePath for $administratorName""" gpresult.exe /SCOPE COMPUTER /X $RsopFilePath /USER $administratorName If (!(Test-Path $RsopFilePath)) { Write-Host """[-] RSoP file was not generated""" throw """RSoP file generation failed""" } } Write-Host """[-] RSoP file generated at $RsopFilePath"""
$Audit = $AuditCSV | ConvertFrom-Csv $AuditC = @() $AuditC += $Audit Write-Host """[-] Needed audit policies converted from csv format"""
[xml]$Xml = Get-Content -Path $RsopFilePath Write-Host """[-] Generated RSoP output:""" Write-Host $Xml.OuterXml $AdvAuditPolicies = $Xml.Rsop.ComputerResults.ExtensionData | where-object { $_.Name.'#text' -Match """Advanced Audit Configuration""" } $Rsop = $AdvAuditPolicies | Select-Object -ExpandProperty Extension | Select-Object -ErrorAction SilentlyContinue -ExpandProperty AuditSetting | Select-Object -Property SubcategoryGuid, SettingValue, SubcategoryName If ($Null -eq $Rsop) { $Rsop = @() } Write-Host """[-] Advanced audit policies extracted from generated file:""" Write-Host $AdvAuditPolicies.OuterXml Write-Host """[-] RSoP extracted from generated file:""" $joinedRsop = $Rsop | Foreach {$_.SubcategoryGuid + ' (' + $_.SubcategoryName + '): ' + $_.SettingValue} Write-Host ($joinedRsop -join ',')
# Getting effective audit policy settings currently in use auditpol.exe /backup /file:"""$($global:TempAuditCsvFileLocation)""" Write-Host """[-] Auditpol output generated at $global:TempAuditCsvFileLocation""" $auditBackupContent = Get-Content -Path $global:TempAuditCsvFileLocation | Where-Object { $_.trim() -ne """""" } $Header = 'Machine Name', 'Policy Target', 'Subcategory', 'Subcategory GUID', 'Inclusion Setting', 'Exclusion Setting', 'Setting Value' $auditBackupContent = $auditBackupContent[1..($auditBackupContent.count - 1)] $SubCat = $auditBackupContent | ConvertFrom-Csv -Header $Header $SubCatC = @() $SubCatC += $SubCat Write-Host """[-] Auditpol output extracted and converted"""
# Merging our **advanced** settings ($AuditC) with potential already existing ($Rsop) if ours risk masking them foreach ($v in $AuditC) { $subGuid = $v.'Subcategory GUID' $subName = $v.'Subcategory'
$existingValue = $SubCatC | where-object { $_.'Subcategory GUID' -match $subGuid } | Select-Object -First 1
If ($Null -eq $existingValue) { Write-Host """[-] No value found in auditpol output for $subName ($subGuid)""" continue }
# Checking if there is currently a setting that blocks something we need If ($existingValue.'Setting Value' -eq 0 -or $existingValue.'Setting Value' -eq 4) { $RsopValue = $Rsop | where-object { $_.SubcategoryGuid -match $subGuid } | Select-Object -First 1 If ($Null -eq $RsopValue) { Write-Host """[-] No value found in RSoP output for $subName ($subGuid)""" continue } If ($RsopValue.SettingValue -eq 0 -or $RsopValue.SettingValue -eq 4) { $SubcategoryError = 'Tenable.ad requires the audit policy ' + $subName + ' but the current AD configuration prevents its usage' throw $SubcategoryError } }
If ($existingValue.'Setting Value' -eq $v.'Setting Value') { Write-Host """[-] Value found in auditpol output is the one needed for $subName ($subGuid)""" continue }
Write-Host """[-] Setting value found in auditpol output to Success and Failure for $subName ($subGuid)""" $v.'Setting Value' = 3 }
# Converting existing basic configuration (not needed) to advanced one If (($Null -eq $AdvAuditPolicies) -Or ($AdvAuditPolicies.Extension.ChildNodes.Count -eq 0)) { Write-Host """[-] Trying to convert existing basic audit policies to advanced ones""" $basicAuditPoliciesToMigrate = """Machine Name,Policy Target,Subcategory,Subcategory GUID,Inclusion Setting,Exclusion Setting,Setting Value""" + """ """ foreach ($auditPoliciesSubCategory in $SubCatC ) { If ($auditPoliciesSubCategory.'Subcategory GUID' -ne '' -And (-Not (($auditPoliciesSubCategory.'Setting Value' -eq 0) -Or ($auditPoliciesSubCategory.'Setting Value' -eq 4)))) { $notNeededRsopValue = $AuditC | where-object { $_.'Subcategory GUID' -match $auditPoliciesSubCategory.'Subcategory GUID' } | Select-Object -First 1 If ($Null -eq $notNeededRsopValue) { $auditPoliciesSubCategoryGuid = $auditPoliciesSubCategory.'Subcategory GUID' $auditPoliciesSubCategoryName = $auditPoliciesSubCategory.'Subcategory' $auditPoliciesSubCategoryValue = $auditPoliciesSubCategory.'Setting Value' Write-Host """[-] Existing audit policy $auditPoliciesSubCategoryName ($auditPoliciesSubCategoryGuid) value will be set to $auditPoliciesSubCategoryValue like previously""" $newBasicSetting = $auditPoliciesSubCategory.'Machine Name' + """,""" + $auditPoliciesSubCategory.'Policy Target' + """,Audit """ + $auditPoliciesSubCategory.Subcategory + """,""" + $auditPoliciesSubCategory.'Subcategory GUID' + """,""" + $auditPoliciesSubCategory.'Inclusion Setting' + """,""" + $auditPoliciesSubCategory.'Exclusion Setting' + """,""" + $auditPoliciesSubCategory.'Setting Value' $basicAuditPoliciesToMigrate = $basicAuditPoliciesToMigrate + $newBasicSetting + """ """ } } } $customCsv = $basicAuditPoliciesToMigrate | ConvertFrom-Csv $CsvC = @() $CsvC += $customCsv $AuditC += $CsvC }
$AuditCSV = $AuditC | ConvertTo-Csv -NoTypeInformation | ForEach-Object { $_.replace('"""', '') } $AuditCSV | Set-Content -Encoding UTF8 -Path $global:AuditCSVPath -ErrorAction Stop Write-Host """[-] Generated audit policies to be deployed: $AuditCSV"""
# Clean task temporary folder Remove-Item -Recurse -Force $global:TemporaryTaskFolderLocation -ErrorAction SilentlyContinue | Out-Null Write-Host """[-] Temporary folder $global:TemporaryTaskFolderLocation cleaned""" } catch { Write-Error $_.Exception } } }
function Protect-IoaFolder { try { $ioaFolderAcl = Get-Acl -Path $global:IoaFolderLocation $ioaFolderAcl.SetAccessRuleProtection($True, $True) Set-Acl -Path $global:IoaFolderLocation -AclObject $ioaFolderAcl Write-Host """[-] Inheritance removed for directory $global:IoaFolderLocation""" $ioaFolderAcl = Get-Acl -Path $global:IoaFolderLocation $ioaFolderAclAccessRules = $ioaFolderAcl.GetAccessRules($True, $True, [System.Security.Principal.SecurityIdentifier]) ForEach ($acl in $ioaFolderAclAccessRules) { $aclIdentitySid = $acl.IdentityReference.Value If ($aclIdentitySid -eq 'S-1-5-11') { $Null = $ioaFolderAcl.RemoveAccessRule($acl) Write-Host '[-] Authenticated users group removed from IOA folder ACLs' } } $tenableAccount = New-Object System.Security.Principal.NTAccount('dcadmin') $tenableAccountSid = $tenableAccount.Translate([System.Security.Principal.SecurityIdentifier]) $tenableAccountSidValue = $tenableAccountSid.Value $tenableAccountValue = $tenableAccount.Value $ruleReadWrite = New-Object System.Security.AccessControl.FileSystemAccessRule($tenableAccountSid, 'ReadAndExecute, Synchronize, Write', 'ContainerInherit, ObjectInherit', 'None', 'Allow') $ioaFolderAcl.AddAccessRule($ruleReadWrite) Write-Host """[-] Tenable.ad service account ($tenableAccountSidValue : $tenableAccountValue) ACL set for IOA folder""" Set-Acl -Path $global:IoaFolderLocation -AclObject $ioaFolderAcl Write-Host """[-] Right permissions set to IOA folder""" } catch { Write-Error $_.Exception } }
function Clean-OldTasks { try { $tenableTasks = Get-ScheduledTask | Where-Object { $_.TaskName.StartsWith("""TenableADTask""") -Or $_.TaskName.StartsWith("""TenableADCleaningTask""") -Or $_.TaskName.StartsWith("""AlsidTask""") -Or $_.TaskName.StartsWith("""AlsidCleaningTask""") } } catch { Write-Host """[-] Get-ScheduledTask cmdlet is missing from this system, cannot clean old task""" return }
If($tenableTasks.Count -gt 1){ $oldtenableTasks = $tenableTasks | Get-ScheduledTaskInfo | Sort-Object LastRunTime -Descending | Select-Object -Skip 1 foreach ($task in $oldtenableTasks) { $taskName = $task.TaskName Unregister-ScheduledTask -TaskName $taskName -Confirm:$False Write-Host """[-] Old task $taskName cleaned""" } } }
function Clean-OldGpo { Write-Host """[-] Old GPO folder $global:OldGpoFolder will be cleaned""" $cleanCommand = """Start-Sleep 10; If(Test-Path '$global:OldGpoFolder') { Remove-Item '$global:OldGpoFolder' -Recurse -Force }""" $bytes = [System.Text.Encoding]::Unicode.GetBytes($cleanCommand) $cleanEncodedCommand = [Convert]::ToBase64String($bytes) Start-Process powershell.exe -ArgumentList @("""-noprofile""", """-executionpolicy""", """bypass""", """-encodedCommand""", $cleanEncodedCommand) }
try {
# Replication may occur after current immediate task is performed # Because immediate task may be pulled directly from another domain controller when a gpupdate happens # As we need the SYSVOL folders associated to our GPO replicated to the local domain controller before starting our process # We must wait for GPO to be replicated before performing this immediated task (24 hours maximum) For ($i = 1; $i -le 288; $i++) {
If ($i -gt 1) { Start-Sleep 300 }
If(Test-Path $global:GptIniLocation) { break }
If ($i -eq 288) { Exit } }
$LogfilePrefix = """TenableTranscript_""" Remove-Item $($envTemp + '\' + $LogfilePrefix + '*') -ErrorAction SilentlyContinue
$Logfile = $LogfilePrefix + $global:EnvMachineName + '_' + [guid]::NewGuid() + '.log' $TranscriptsFolder = $global:IoaFolderLocation + '\transcripts' If($PSVersionTable.PSVersion.Major -ge 5) { Start-Transcript -Path $($TranscriptsFolder + '\' + $Logfile) -IncludeInvocationHeader Start-Transcript -Path $($envTemp + '\' + $Logfile) -IncludeInvocationHeader } Else { # cannot Start-Transcript twice on older versions Start-Transcript -Path $($TranscriptsFolder + '\' + $Logfile) }
Write-Host """[-] Starting script version v3.40.0-alpha.21, occurrence 0"""
Write-Host """[-] Starting immediate task on DC $global:EnvMachineName""" Write-Host """[-] Domain controller DNS name: $global:LocalDomainControllerDNSName""" Write-Host """[-] Primary domain controller: $global:PrimaryDomainControllerFilter""" Write-Host """[-] Primary domain controller site: $global:PrimaryDomainControllerSite""" Write-Host """[-] Current domain controller site: $global:LocalDomainControllerSite""" Write-Host """[-] Audit policies folder: $global:AuditPath""" Write-Host """[-] Temporary folder: $global:TemporaryTaskFolderLocation""" Write-Host """[-] IOA folder location: $global:IoaFolderLocation"""
# Cast to string for 2008R2 If ($True) { Write-Host """[-] File audit.csv will be generated by the listener""" } ElseIf (!(Test-Path $global:AuditCSVPath) -Or ((Get-Content $global:AuditCSVPath) -eq $NULL)) { Write-Host """[-] Generating audit.csv on first domain controller $global:EnvMachineName""" Generate-AuditCsv Write-Host """[-] Running gpupdate /force""" Invoke-Command -ScriptBlock { gpupdate /force } } Else { Write-Host """[-] File audit.csv already exists""" } Protect-IoaFolder Register-Channels Register-EventListener Clean-OldTasks If($global:DeleteOldGpo) { Clean-OldGpo } } catch { Write-Error $_.Exception } finally { Write-Host """[+] All done! """ Stop-Transcript If($PSVersionTable.PSVersion.Major -ge 5) { # we started two transcripts, so we stop twice Stop-Transcript } }})
